import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators ,ValidatorFn, AbstractControl } from '@angular/forms';

@Component({
  selector: 'app-user-register',
  templateUrl: './user-register.component.html',
  styleUrls: ['./user-register.component.css']
})
export class UserRegisterComponent implements OnInit{
  registerationForm!: FormGroup;
  constructor(){

  }
  ngOnInit() {
    this.registerationForm = new FormGroup({
      userName: new FormControl(null, Validators.required),
      email: new FormControl(null, [Validators.required, Validators.email]),
      password: new FormControl(null, [Validators.required, Validators.minLength(8)]),
      onfirmPassword: new FormControl(null, [Validators.required, this.passwordMatchingValidatior()]),
      mobile: new FormControl(null, [Validators.required, Validators.maxLength(10)])
    });
  }

  passwordMatchingValidatior(): ValidatorFn {
    return (control: AbstractControl): { [key: string]: any } | null => {
      const password = this.registerationForm.get('password')!.value;
      const confirmPassword = control.value;
      return password === confirmPassword ? null : { passwordNotMatched: true };
    };
  }

  // ------------------------------------
  // Getter methods for all form controls
  // ------------------------------------
  get userName() {
    return this.registerationForm.get('userName') as FormControl;
  }

  get email() {
    return this.registerationForm.get('email') as FormControl;
  }
  get password() {
    return this.registerationForm.get('password') as FormControl;
  }
  get confirmPassword() {
    return this.registerationForm.get('confirmPassword') as FormControl;
  }
  get mobile() {
    return this.registerationForm.get('mobile') as FormControl;
  }
  // ------------------------

  onSubmit() {
    console.log(this.registerationForm);
  }
}
