import { Component, OnInit ,ViewChild} from '@angular/core';
import { NgForm } from '@angular/forms';
import {Router} from '@angular/router';
@Component({
  selector: 'app-add-property',
  templateUrl: './add-property.component.html',
  styleUrls: ['./add-property.component.css']
})
export class AddPropertyComponent implements OnInit {
 @ViewChild('Form') addPropertyForm!: NgForm;
  constructor(private router: Router) { }

  ngOnInit() {
    this.addPropertyForm.controls['name'].setValue('Default Valur');
  }

  onBack(){
    this.router.navigate(['/']);
  }

  onSubmit(){
    console.log(this.addPropertyForm);
  }
}

<div class="card">
  <div class="card-header">
    Add New Property

  </div>
  <div class="card-body">
    <form (ngSubmit)='onSubmit()' #Form="ngForm" >
      <div class="form-group col-12">
        <label for="name">name</label>
        <!-- minlength = valid length of text -->
        <!-- #propName is use JsObject assige variable to text -->
        <input type="text" class="form-control" ngModel minlength="5" 
        #propName="ngModel" name="Name" required>
        <span *ngIf="!propName.valid && propName.touched" class="error-block">
          <span *ngIf="propName.errors?.['required']"> Please provide name </span>
          <span *ngIf="propName.errors?.['minlength']"> Name must be atleast 5 char long</span>
        </span>
      </div>
      <div class="form-group col-12">
        <label for="type">type</label>
        <input type="text" class="form-control" ngModel name="Type">
      </div>
      <div class="form-group col-12">
        <label for="price">price</label>
        <input type="text" class="form-control" ngModel name="Price">
      </div>
      <div class="form-group col-12">
        <button [disabled]="!Form.valid" type="submit" class="btn btn-primary mr-3">Save</button>
        <button type="reset" class="btn btn-primary ml-3">Cancle</button>
      </div>

    </form>
  </div>
</div>
<p>
  add-property works!
  <button (click)="onBack()" class="btn btn-primary">Back</button>
</p>
