import { Component, OnInit ,ViewChild} from '@angular/core';
import { FormBuilder, FormControl, FormGroup, NgForm, Validators } from '@angular/forms';
import {Router} from '@angular/router';
import { TabsetComponent } from 'ngx-bootstrap/tabs';
import { IPropertyBase } from 'src/app/Interface/IPropertybase';
@Component({
  selector: 'app-add-property',
  templateUrl: './add-property.component.html',
  styleUrls: ['./add-property.component.css']
})
export class AddPropertyComponent implements OnInit {
//  @ViewChild('Form') addPropertyForm!: NgForm;
 @ViewChild('formTabs') formTabs?: TabsetComponent;
 addPropertyForm!: FormGroup;
 nextClick!: boolean;

 // Will come from masters
  propertyTypes: Array<string> = [ "Single-Family Homes", "Shop Houses","Town house","Apartment","Condominium","Cooperative Housing","Mobile Home","Time-Share Homes",]
  furnishTypes: Array<string> = ['Fully', 'Semi', 'Unfurnished']

  propertyView: IPropertyBase = {
    Id: null,
    Name: '',
    Price: null,
    SellRent: null,
    PType: null,
    FType: null,
    BHK: null,
    BuiltArea: null,
    City: null,
    RTM: null
  };


  constructor(private router: Router ,private dataFB: FormBuilder) { }

  ngOnInit() {
    this.CreateAddPropertyForm();
  }

  CreateAddPropertyForm(){
    this.addPropertyForm = this.dataFB.group({
      BasicInfo: this.dataFB.group({
        SellRent: [null, Validators.required],
        PType: [null, Validators.required],
        Name: [null, Validators.required]}),
      PriceInfo: this.dataFB.group({
        Price: [null, Validators.required],
        BuiltArea: [null, Validators.required]
      }),
    })
  }

  /////// Gettet Method //////

  get BasicInfo(){
    return this.addPropertyForm.controls['BasicInfo'] as FormGroup;
  }

  get SellRent(){
    return this.BasicInfo.controls['SellRent'] as FormControl;
  }

  ////////////////////////////
  onBack() {
    this.router.navigate(['/']);
  }

  onSubmit() {
    console.log('Congrats, form Submitted');
    console.log('SellRent=' + this.addPropertyForm.value.BasicInfo.SellRent);
    console.log(this.addPropertyForm);
  }

  // selectTab(tabId: number, IsCurrentTabValid: boolean) {
  //   this.nextClick = true;
  //   if(IsCurrentTabValid){
  //      if (this.formTabs?.tabs[tabId]) {
  //     this.formTabs.tabs[tabId].active = true;
  //   }
  //   }

  // }
  selectTab(tabId: number, IsCurrentTabValid: boolean) {
    if (!IsCurrentTabValid) {
      // Display an error message or perform appropriate action
      return;
    }
    this.nextClick = false; // Reset nextClick when switching tabs
    if (this.formTabs?.tabs[tabId]) {
      this.formTabs.tabs[tabId].active = true;
    }
  }
}
