import { Component, OnInit } from '@angular/core';
import {ActivatedRoute, Router} from '@angular/router';
@Component({
  selector: 'app-property-detail',
  templateUrl: './property-detail.component.html',
  styleUrls: ['./property-detail.component.css']
})
export class PropertyDetailComponent implements OnInit {

  public propertyId!: number ;

  constructor( private route: ActivatedRoute, private router: Router) { }

  ngOnInit() {
    this.propertyId =+ this.route.snapshot.params['id'];
    // this.propertyId = Number(this.route.snapshot.params['id']);
    // this.propertyId = this.route.snapshot.params['id']; 

    this.route.params.subscribe(
      (params) =>{
        this.propertyId = +params['id'];
      }
    )
    // button next page connect to params with subscrib function
  }

  onSelectNext(){
    // this.propertyId++ ;
    this.propertyId +=1 ;
    this.router.navigate(['property-detail',this.propertyId])
  }
}
<p>
  property-detail works!
  your are selecting {{propertyId}}
  <br>
  <button (click)="onSelectNext()" class="btn btn-primary"> Next House</button>

  <br>
 <button routerLink="/property-datail/1" class="btn btn-primary">1</button>


</p>
